/**
 * 从数据库加载配置信息。
 * Load configs from database and save it to config->system and config->personal.
 *
 * @access public
 * @return void
 */
public function loadConfigFromDB()
{
    /* Get configs of system and current user. */
    $account = isset($this->app->user->account) ? $this->app->user->account : '';
    if ($this->config->db->name) {
        $config = $this->loadModel('setting')->getSysAndPersonalConfig($account);
    }
    $this->config->system   = isset($config['system']) ? $config['system'] : [];
    $this->config->personal = isset($config[$account]) ? $config[$account] : [];

    $this->commonTao->updateDBWebRoot($this->config->system);

    /* Override the items defined in config/config.php and config/my.php. */
    if (isset($this->config->system->common)) {
        $this->app->mergeConfig($this->config->system->common, 'common');
    }
    if (isset($this->config->personal->common)) {
        $this->app->mergeConfig($this->config->personal->common, 'common');
    }

    $this->config->disabledFeatures = $this->config->disabledFeatures . ',' . $this->config->closedFeatures;
}

