public static String getRequestStringEx(String parameter) {
        try {
            HttpServletRequest request = getRequest();
            if (request == null) {
                return null;
            } else {
                request.setCharacterEncoding("UTF-8");
                String param = request.getParameter(parameter);
                return param != null ? URLDecoder.decode(param, StandardCharsets.UTF_8) : null;
            }
        } catch (Exception var3) {
            Exception e = var3;
            log.error(e.getMessage(), e);
            return null;
        }
    }

public AjaxDto<HelpCenterUserAccount> loadAllUserInfo(String name, Pagination pagination, boolean isAdmin) throws Exception {
        AjaxDto<HelpCenterUserAccount> infos = this.helpCenterDao.loadAllUserInfo(name, pagination, isAdmin);
        return infos;
    }

public AjaxDto<HelpCenterUserAccount> loadAllUserInfo(String name, Pagination pagination, boolean isAdmin) throws Exception {
    List<QueryParam> queryParams = new ArrayList();
    StringBuilder sql = new StringBuilder();
    sql.append("SELECT a.ID AS id,a.account AS account,a.account_type AS accountType,a.use_status AS useStatus,a.user_Name as userName,");
    sql.append("a.create_time AS createTime,a.update_time AS updateTime FROM jt808_help_center_account AS a WHERE 1=1 and a.use_status <2 ");
    if (StringUtils.isNotBlank(name)) {
        sql.append(" and a.account LIKE '%").append(name).append("%'");
    }

    if (!isAdmin) {
        sql.append(" and a.account_type != 0");
    }

    return this.findByPage(sql.toString(), pagination, HelpCenterUserAccount.class, queryParams);
}

public <T> AjaxDto<T> findByPage(String sql, Pagination pagination, Class<T> voClazz, List<QueryParam> queryParams) throws Exception {
        String countSql = null;
        if (pagination != null) {
            countSql = String.format("SELECT COUNT(*) FROM (%s) sss", sql);
        }

        List<QueryScalar> scalars = QueryScalarMapper.loadMapping(voClazz, sql);
        if (pagination != null && pagination.getSortParams() != null && !pagination.getSortParams().isEmpty()) {
            sql = this.handlerSortParams(sql, pagination.getSortParams());
        }

        return this.paginationDao.getExtraByNativeSqlEx(sql, pagination, scalars, voClazz, countSql, queryParams);
    }

public AjaxDto getExtraByNativeSqlEx(String queryString, Pagination pagination, List<QueryScalar> scalars, Class target, String countString, List<QueryParam> queryParams) {
        AjaxDto ajaxDto = null;
        HibernateCallback action = new HibernatePaginationCallback(queryString, pagination, scalars, target, countString, queryParams);
        ajaxDto = (AjaxDto)this.getHibernateTemplate().execute(action);
        ajaxDto = this.setAjaxDto(ajaxDto, pagination);
        return ajaxDto;
    }
