public class MobController extends HttpServlet {
    private static final long serialVersionUID = 2468343897778461694L;

    public MobController() {
    }

    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
        MobHttpOperator operator = MobHttpOperator.getInstance();
        MobDispatcher dispatcher = new MobDispatcher();
        dispatcher.doAction(operator);
    }
}

public String getAction() {
        String path = this.request.getRequestURI();
        int end = path.indexOf("/nccloud/mob/");
        if (end == -1) {
            String message = NCLangRes4VoTransl.getNCLangRes().getStrByID("1501001_0", "01501001-0205");
            ExceptionUtils.wrapBusinessException(message);
        }

        String[] paths = path.split("/nccloud/mob/");
        String[] actionPath = paths[1].split("/");
        if (actionPath.length < 3) {
            String message = NCLangRes4VoTransl.getNCLangRes().getStrByID("1501001_0", "01501001-0205");
            ExceptionUtils.wrapBusinessException(message);
        }

        path = actionPath[actionPath.length - 3] + "/" + actionPath[actionPath.length - 2] + "/" + actionPath[actionPath.length - 1];
        return path;
    }
