import com.alibaba.druid.sql.ast.SQLStatement;
import com.alibaba.druid.util.JdbcConstants;

import java.util.List;

public class DruidReconfigureTest {
    public static void main(String[] args) {
        // 测试MSSQL方言的RECONFIGURE语句
        testReconfigure(String.valueOf(JdbcConstants.SQL_SERVER), "EXEC sp_configure 'show advanced options', 1; RECONFIGURE;");

        // 测试普通SQL语句作为对比
        testReconfigure(String.valueOf(JdbcConstants.SQL_SERVER), "SELECT * FROM users;");
    }

    private static void testReconfigure(String dbType, String sql) {
        System.out.println("\n=== 测试方言: " + dbType + " ===");
        System.out.println("SQL: " + sql);

        try {
            List<SQLStatement> statements = SQLUtils.parseStatements(sql, dbType);

            if (statements.isEmpty()) {
                System.out.println("解析结果: 无法解析任何SQL语句");
            } else {
                System.out.println("解析结果: 成功解析 " + statements.size() + " 条语句");
                for (int i = 0; i < statements.size(); i++) {
                    SQLStatement stmt = statements.get(i);
                    System.out.println("  语句 #" + (i+1) + ": " + stmt.getClass().getSimpleName());
                    System.out.println("  SQL文本: " + SQLUtils.toSQLString(stmt, dbType));
                }
            }
        } catch (Exception e) {
            System.out.println("解析异常: " + e.getMessage());
            e.printStackTrace(System.out);
        }
    }
}  


